import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Sidebar.css';

export default function Sidebar() {
  const location = useLocation();
  const [open, setOpen] = useState(true);
  const user = localStorage.getItem('user');
  if (!user) return null;

  const item = (to, label, emoji) => (
    <li className={location.pathname === to ? 'active' : ''}>
      <Link to={to} onClick={() => setOpen(false)}>
        <span className="icon" aria-hidden>{emoji}</span>
        <span className="text">{label}</span>
      </Link>
    </li>
  );

  // Reflect sidebar state on the body for layout padding control
  useEffect(() => {
    const body = document.body;
    if (open) {
      body.classList.add('sidebar-open');
    } else {
      body.classList.remove('sidebar-open');
    }
    return () => body.classList.remove('sidebar-open');
  }, [open]);

  return (
    <aside className={`sidebar ${open ? 'open' : ''}`}>      
      <div className="top">
        <div className="brand">üß† MindSync</div>
        <button
          className="toggle"
          aria-label="Toggle sidebar"
          aria-expanded={open}
          onClick={() => setOpen(!open)}
        >
          <span className="toggle-bars" aria-hidden>‚â°</span>
        </button>
      </div>
      <nav className="nav">
        <ul>
          {item('/journal', 'Journaling', '‚úçÔ∏è')}
          {item('/entries', 'Entries', 'üìö')}
          {item('/insights', 'Insights', 'üìä')}
          {item('/goals', 'Goals', 'üéØ')}
          {item('/settings', 'Settings', '‚öôÔ∏è')}
        </ul>
      </nav>
      <div className="foot">
        <div className="user">Signed in as <strong>{user}</strong></div>
      </div>
    </aside>
  );
}
