import React, { useState, useEffect } from 'react';
import './Goals.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

function Goals() {
  const [goals, setGoals] = useState([]);
  const [newGoal, setNewGoal] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const [newCategory, setNewCategory] = useState('health');
  const [newTargetDate, setNewTargetDate] = useState('');
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  const userId = localStorage.getItem('userId');
  const authToken = localStorage.getItem('authToken');

  // Fetch goals
  useEffect(() => {
    if (!userId) {
      setLoading(false);
      return;
    }
    
    const fetchGoals = async () => {
      try {
        const response = await fetch(`${API_URL}/api/goals/user/${userId}`, {
          headers: {
            'Authorization': `Bearer ${authToken}`
          }
        });
        if (response.ok) {
          const data = await response.json();
          setGoals(data);
        }
      } catch (err) {
        console.error('Error fetching goals:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchGoals();
  }, [userId, authToken]);

  const addGoal = async () => {
    if (!newGoal.trim()) {
      setMessage('Goal title is required');
      return;
    }

    try {
      const response = await fetch(`${API_URL}/api/goals`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${authToken}`
        },
        body: JSON.stringify({
          userId,
          title: newGoal,
          description: newDescription,
          category: newCategory,
          targetDate: newTargetDate,
          progress: 0,
          status: 'active'
        })
      });

      if (response.ok) {
        const newGoalData = await response.json();
        setGoals([newGoalData, ...goals]);
        setNewGoal('');
        setNewDescription('');
        setNewCategory('health');
        setNewTargetDate('');
        setMessage('✓ Goal added successfully');
        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage('Failed to add goal');
      }
    } catch (err) {
      console.error('Error adding goal:', err);
      setMessage('Error adding goal');
    }
  };

  const updateProgress = async (goalId, progress) => {
    try {
      const response = await fetch(`${API_URL}/api/goals/${goalId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${authToken}`
        },
        body: JSON.stringify({ progress: Math.min(100, Math.max(0, progress)) })
      });

      if (response.ok) {
        const updatedGoal = await response.json();
        setGoals(goals.map(g => g._id === goalId ? updatedGoal : g));
      }
    } catch (err) {
      console.error('Error updating goal:', err);
    }
  };

  const updateStatus = async (goalId, status) => {
    try {
      const response = await fetch(`${API_URL}/api/goals/${goalId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${authToken}`
        },
        body: JSON.stringify({ status })
      });

      if (response.ok) {
        const updatedGoal = await response.json();
        setGoals(goals.map(g => g._id === goalId ? updatedGoal : g));
      }
    } catch (err) {
      console.error('Error updating status:', err);
    }
  };

  const deleteGoal = async (goalId) => {
    try {
      const response = await fetch(`${API_URL}/api/goals/${goalId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${authToken}`
        }
      });

      if (response.ok) {
        setGoals(goals.filter(g => g._id !== goalId));
        setMessage('✓ Goal deleted');
        setTimeout(() => setMessage(''), 3000);
      }
    } catch (err) {
      console.error('Error deleting goal:', err);
    }
  };



  return (
    <div className="goals-container">
      <h1>Goals & Habits</h1>

      <div className="goals-section">
        <h2>Your Goals</h2>
        <div className="add-goal">
          <input
            type="text"
            value={newGoal}
            onChange={(e) => setNewGoal(e.target.value)}
            placeholder="Enter a new goal..."
          />
          <button onClick={addGoal}>Add Goal</button>
        </div>

        <div className="goals-list">
          {goals.map(goal => (
            <div key={goal.id} className="goal-item">
              <h3>{goal.text}</h3>
              <div className="progress-bar">
                <div
                  className="progress-fill"
                  style={{ width: `${goal.progress}%` }}
                ></div>
              </div>
              <input
                type="number"
                min="0"
                max="100"
                value={goal.progress}
                onChange={(e) => updateProgress(goal.id, parseInt(e.target.value))}
                placeholder="Progress %"
              />
              <p>Streak: {goal.streak} days</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Goals;
