import React, { useEffect, useMemo, useState } from 'react';
import CryptoJS from 'crypto-js';
import './Entries.css';

export default function Entries() {
  const [entries, setEntries] = useState([]);
  const [query, setQuery] = useState('');

  useEffect(() => {
    const user = localStorage.getItem('user');
    if (!user) return;
    const encryptedEntries = JSON.parse(localStorage.getItem(`${user}_entries`) || '[]');
    const decryptedEntries = encryptedEntries.map((e) => {
      try {
        const bytes = CryptoJS.AES.decrypt(e, 'secret-key');
        return JSON.parse(bytes.toString(CryptoJS.enc.Utf8));
      } catch {
        return null;
      }
    }).filter(Boolean);
    // newest first
    decryptedEntries.sort((a,b) => new Date(b.date) - new Date(a.date));
    setEntries(decryptedEntries);
  }, []);

  const filtered = useMemo(() => {
    const q = query.trim().toLowerCase();
    if (!q) return entries;
    return entries.filter((e) =>
      (e.text || '').toLowerCase().includes(q) ||
      (e.title || '').toLowerCase().includes(q) ||
      (e.tags || []).some((t) => t.toLowerCase().includes(q))
    );
  }, [entries, query]);

  const getSentimentColor = (sentiment) => {
    switch(sentiment) {
      case 'positive': return '#22c55e';
      case 'negative': return '#ef4444';
      case 'neutral': return '#60a5fa';
      default: return '#9ca3af';
    }
  };

  const getSentimentEmoji = (sentiment) => {
    switch(sentiment) {
      case 'positive': return 'üòä';
      case 'negative': return 'üòî';
      case 'neutral': return 'üòê';
      default: return 'üìù';
    }
  };

  return (
    <div className="entries-page">
      <div className="entries-header">
        <h1>üìö All Entries</h1>
        <p className="header-subtitle">Your complete diary of thoughts and moments</p>
      </div>

      <div className="search-container">
        <input
          type="text"
          placeholder="üîç Search entries by text, title or tag..."
          value={query}
          onChange={(e)=>setQuery(e.target.value)}
          className="search-input"
        />
      </div>

      {filtered.length === 0 ? (
        <div className="empty-state">
          <p className="empty-icon">üì≠</p>
          <p className="empty-text">{query ? 'No matching entries found.' : 'No entries yet. Start journaling!'}</p>
        </div>
      ) : (
        <div className="entries-grid">
          {filtered.map((e) => (
            <article key={e.id} className="entry-card">
              <div className="entry-header">
                <div className="entry-meta">
                  <span className="entry-date">
                    üìÖ {new Date(e.date).toLocaleDateString('en-US', { 
                      year: 'numeric', 
                      month: 'short', 
                      day: 'numeric',
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </span>
                </div>
                <span 
                  className="sentiment-badge"
                  style={{ backgroundColor: `${getSentimentColor(e.sentiment)}20`, borderColor: getSentimentColor(e.sentiment) }}
                >
                  <span className="sentiment-emoji">{getSentimentEmoji(e.sentiment)}</span>
                  <span className="sentiment-text">{e.sentiment || 'neutral'}</span>
                </span>
              </div>

              {e.title && (
                <h2 className="entry-title">{e.title}</h2>
              )}

              <p className="entry-text">{e.text.substring(0, 200)}{e.text.length > 200 ? '...' : ''}</p>

              {(e.images?.length > 0 || e.files?.length > 0) && (
                <div className="media-section">
                  {e.images?.length > 0 && (
                    <div className="images-preview">
                      {e.images.slice(0, 4).map((img, idx) => (
                        <div key={idx} className="image-thumbnail">
                          <img src={img.dataUrl} alt={`Entry ${idx}`} />
                          {e.images.length > 4 && idx === 3 && (
                            <div className="more-indicator">+{e.images.length - 4}</div>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                  
                  {e.files?.length > 0 && (
                    <div className="files-list">
                      {e.files.map((f, idx) => (
                        <a 
                          key={idx} 
                          href={f.dataUrl} 
                          download={f.name}
                          className="file-link"
                        >
                          <span className="file-icon">üìé</span>
                          <span className="file-name">{f.name}</span>
                        </a>
                      ))}
                    </div>
                  )}
                </div>
              )}

              {e.tags?.length > 0 && (
                <div className="tags-container">
                  {e.tags.slice(0, 5).map((t, i) => (
                    <span key={i} className="tag">{t}</span>
                  ))}
                </div>
              )}
            </article>
          ))}
        </div>
      )}
    </div>
  );
}
