import React, { useEffect, useMemo, useState } from "react";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

const getMoodEmoji = (mood) => {
  const map = {
    positive: "üòä",
    negative: "üòî",
    neutral: "üòê",
    happy: "üòÑ",
    sad: "üò¢",
    angry: "üò†",
    excited: "ü§©",
    calm: "üòå",
  };
  return map[mood] || "üìù";
};

export default function Entries() {
  const [entries, setEntries] = useState([]);
  const [query, setQuery] = useState("");
  const [moodFilter, setMoodFilter] = useState("all");
  const [loading, setLoading] = useState(true);
  const [streak, setStreak] = useState({ current: 0, longest: 0 });

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (!userId) return setLoading(false);

    fetch(`${API_URL}/api/entries/user/${userId}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("authToken")}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setEntries(data);
        else {
          setEntries(data.entries);
          setStreak(data.streak);
        }
      })
      .finally(() => setLoading(false));
  }, []);

  const filtered = useMemo(() => {
    return entries.filter((e) => {
      if (moodFilter !== "all" && e.mood !== moodFilter) return false;
      if (!query) return true;
      const q = query.toLowerCase();
      return (
        e.title?.toLowerCase().includes(q) ||
        e.content?.toLowerCase().includes(q)
      );
    });
  }, [entries, query, moodFilter]);

  if (loading) {
    return (
      <div style={styles.loader}>
        <div style={styles.spinner}>‚è≥</div>
        <p>Loading entries...</p>
      </div>
    );
  }

  return (
    <div style={styles.page}>
      <h1 style={styles.title}>üìî Journal Entries</h1>

      {/* Stats */}
      <div style={styles.stats}>
        <Stat label="Total" value={entries.length} />
        <Stat label="Streak" value={streak.current} />
        <Stat label="Best" value={streak.longest} />
      </div>

      {/* Search */}
      <input
        style={styles.search}
        placeholder="Search entries..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />

      {/* Mood filter */}
      <div style={styles.filters}>
        {["all", ...new Set(entries.map((e) => e.mood))].map((m) => (
          <button
            key={m}
            onClick={() => setMoodFilter(m)}
            style={{
              ...styles.filterBtn,
              ...(moodFilter === m ? styles.filterActive : {}),
            }}
          >
            {m === "all" ? "All" : `${getMoodEmoji(m)} ${m}`}
          </button>
        ))}
      </div>

      {/* Entries */}
      <div style={styles.grid}>
        {filtered.map((e) => (
          <div key={e._id} style={styles.card}>
            <div style={styles.cardHeader}>
              <span style={styles.emoji}>{getMoodEmoji(e.mood)}</span>
              <span style={styles.date}>
                {new Date(e.createdAt).toDateString()}
              </span>
            </div>

            {e.title && <h3 style={styles.cardTitle}>{e.title}</h3>}

            <p style={styles.cardText}>
              {e.content.length > 140
                ? e.content.slice(0, 140) + "..."
                : e.content}
            </p>

            <div style={styles.tags}>
              {e.tags?.slice(0, 3).map((t, i) => (
                <span key={i} style={styles.tag}>
                  #{t}
                </span>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

/* Small components */
const Stat = ({ label, value }) => (
  <div style={styles.statCard}>
    <div style={styles.statValue}>{value}</div>
    <div style={styles.statLabel}>{label}</div>
  </div>
);

/* Styles */
const styles = {
  page: {
    padding: "32px",
    fontFamily: "system-ui, sans-serif",
    background: "#f9fafb",
    minHeight: "100vh",
  },
  title: {
    fontSize: "32px",
    fontWeight: 600,
    marginBottom: "20px",
  },
  stats: {
    display: "flex",
    gap: "16px",
    marginBottom: "24px",
  },
  statCard: {
    background: "#fff",
    padding: "16px",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0,0,0,0.06)",
    textAlign: "center",
    minWidth: "120px",
  },
  statValue: {
    fontSize: "22px",
    fontWeight: 600,
  },
  statLabel: {
    fontSize: "13px",
    color: "#6b7280",
  },
  search: {
    width: "100%",
    padding: "12px",
    borderRadius: "8px",
    border: "1px solid #e5e7eb",
    marginBottom: "16px",
  },
  filters: {
    display: "flex",
    gap: "8px",
    flexWrap: "wrap",
    marginBottom: "24px",
  },
  filterBtn: {
    padding: "6px 14px",
    borderRadius: "999px",
    border: "1px solid #e5e7eb",
    background: "#fff",
    cursor: "pointer",
  },
  filterActive: {
    background: "#111827",
    color: "#fff",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,minmax(280px,1fr))",
    gap: "20px",
  },
  card: {
    background: "#fff",
    padding: "20px",
    borderRadius: "16px",
    boxShadow: "0 10px 20px rgba(0,0,0,0.08)",
    transition: "transform .25s",
  },
  cardHeader: {
    display: "flex",
    justifyContent: "space-between",
    marginBottom: "8px",
  },
  emoji: {
    fontSize: "22px",
  },
  date: {
    fontSize: "12px",
    color: "#6b7280",
  },
  cardTitle: {
    fontSize: "18px",
    fontWeight: 600,
    margin: "8px 0",
  },
  cardText: {
    fontSize: "14px",
    color: "#374151",
    lineHeight: 1.6,
  },
  tags: {
    marginTop: "12px",
    display: "flex",
    gap: "6px",
    flexWrap: "wrap",
  },
  tag: {
    background: "#f3f4f6",
    padding: "4px 10px",
    borderRadius: "12px",
    fontSize: "12px",
  },
  loader: {
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "system-ui",
  },
  spinner: {
    fontSize: "40px",
    animation: "spin 2s linear infinite",
  },
};
