import React, { useState, useRef, useEffect } from 'react';
import './MemoTalks.css';

const MemoTalks = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      sender: 'memotalks',
      text: "Hello, I'm MemoTalks. I'm here to listen, support, and guide you through your thoughts and feelings. Share what's on your mind."
    }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Generate dynamic response based on user input
  const generateDynamicResponse = (userMessage) => {
    const lowerMessage = userMessage.toLowerCase();

    // Emotion detection keywords
    const sadKeywords = ['sad', 'depressed', 'down', 'lonely', 'miserable', 'terrible', 'awful', 'hurts', 'pain', 'cry', 'broken', 'lost', 'hopeless'];
    const happyKeywords = ['happy', 'great', 'amazing', 'wonderful', 'excited', 'proud', 'love', 'celebrate', 'success', 'win', 'achieved'];
    const anxiousKeywords = ['doubt', 'unsure', 'uncertain', 'confused', 'worried', 'anxious', 'scared', 'afraid', 'nervous', 'stressed'];
    const motivationKeywords = ['motivate', 'inspire', 'encourage', 'stuck', 'lazy', 'procrastinating'];

    // Topic detection keywords
    const goalKeywords = ['goal', 'target', 'objective', 'dream', 'want', 'achieve', 'improve'];
    const relationshipKeywords = ['friend', 'family', 'love', 'relationship', 'partner', 'parent', 'sibling'];
    const workKeywords = ['work', 'job', 'career', 'boss', 'colleague', 'project', 'deadline', 'office'];
    const healthKeywords = ['health', 'fitness', 'exercise', 'sleep', 'mental', 'physical', 'wellness'];

    // Detect emotion
    let emotion = 'neutral';
    if (sadKeywords.some(keyword => lowerMessage.includes(keyword))) emotion = 'sad';
    else if (happyKeywords.some(keyword => lowerMessage.includes(keyword))) emotion = 'happy';
    else if (anxiousKeywords.some(keyword => lowerMessage.includes(keyword))) emotion = 'anxious';
    else if (motivationKeywords.some(keyword => lowerMessage.includes(keyword))) emotion = 'motivation';

    // Detect topic
    let topic = 'general';
    if (goalKeywords.some(keyword => lowerMessage.includes(keyword))) topic = 'goals';
    else if (relationshipKeywords.some(keyword => lowerMessage.includes(keyword))) topic = 'relationships';
    else if (workKeywords.some(keyword => lowerMessage.includes(keyword))) topic = 'work';
    else if (healthKeywords.some(keyword => lowerMessage.includes(keyword))) topic = 'health';

    // Response templates based on emotion and topic
    const responses = {
      sad: {
        goals: [
          `I hear you're feeling down about your goals. That's completely normal - setbacks are part of growth. üíô What small step could you take today?`,
          `It sounds like your goals feel overwhelming right now. üå± Let's break things down - what's one achievable thing you could do?`,
          `Your sadness shows you care about your goals. üí™ Remember, every successful person has faced this. What's your next move?`
        ],
        relationships: [
          `Relationships can be complicated. üíô It seems like something's weighing on you. Want to talk about what's happening?`,
          `I can sense the pain in what you're sharing. üå± Remember, healthy communication can help. What would help right now?`,
          `Your feelings about relationships matter. üíï What would make you feel more connected?`
        ],
        work: [
          `Work stress is real and your feelings are valid. üíô What's the hardest part right now?`,
          `It sounds like work is draining you. üå± Remember, your well-being comes first. What would help?`,
          `Career challenges can feel overwhelming. üí™ Let's focus on what you can control. What's one thing?`
        ],
        health: [
          `Your health concerns are important. üíô Self-care starts with listening to yourself. What do you need?`,
          `I hear you're struggling with your health. üå± Small steps matter - what's one thing you could do?`,
          `Your wellness journey is unique. üí™ What support would help you most right now?`
        ],
        general: [
          `I can sense you're going through something difficult. üíô You're not alone. What's on your heart?`,
          `Thank you for sharing. Your feelings are valid. üå± What would help you feel better?`,
          `It takes courage to acknowledge sadness. üí™ Remember, this is temporary. What would comfort you?`
        ]
      },
      happy: {
        goals: [
          `That's wonderful! üéâ Your enthusiasm about your goals is inspiring. Keep that momentum going!`,
          `Amazing! üöÄ You're on the right path. What's your next milestone?`,
          `I love your energy! üí´ You're clearly making progress. What's next on your journey?`
        ],
        relationships: [
          `That's beautiful! üíï It's wonderful when relationships bring us joy. Cherish these moments!`,
          `Your happiness is contagious! üéâ It sounds like you're in a great place. Keep nurturing that!`,
          `This joy shows the strength of your connections. üí´ That's truly special!`
        ],
        work: [
          `Fantastic! üéâ Your passion for your work is clear. Keep thriving!`,
          `That's excellent! üöÄ You're doing amazing things. What's next?`,
          `Your work satisfaction is wonderful! üí™ Keep this positive energy going!`
        ],
        health: [
          `That's fantastic! üí™ Your commitment to wellness is inspiring!`,
          `Wonderful! üéâ You're taking great care of yourself. Keep it up!`,
          `This is amazing! üåü Your health progress deserves celebration!`
        ],
        general: [
          `That's wonderful! üéâ Your joy is infectious. Celebrate this moment!`,
          `Amazing! üí´ You deserve to feel this good. Keep shining!`,
          `This is beautiful! üíï Your happiness matters. Embrace it fully!`
        ]
      },
      anxious: {
