import React, { useState, useEffect } from 'react';
import './Goals.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

function Goals() {
  const [goals, setGoals] = useState([]);
  const [newGoal, setNewGoal] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const [newCategory, setNewCategory] = useState('health');
  const [newTargetDate, setNewTargetDate] = useState('');
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  const userId = localStorage.getItem('userId');
  const authToken = localStorage.getItem('authToken');

  // Fetch goals
  useEffect(() => {
    if (!userId) {
      setLoading(false);
      return;
    }
    
    const fetchGoals = async () => {
      try {
        const response = await fetch(`${API_URL}/api/goals/user/${userId}`, {
          headers: {
            'Authorization': `Bearer ${authToken}`
          }
        });
        if (response.ok) {
          const data = await response.json();
          setGoals(data);
        }
      } catch (err) {
        console.error('Error fetching goals:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchGoals();
  }, [userId, authToken]);

  const addGoal = async () => {
    if (!newGoal.trim()) {
      setMessage('Goal title is required');
      return;
    }

    try {
      const response = await fetch(`${API_URL}/api/goals`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${authToken}`
        },
        body: JSON.stringify({
          userId,
          title: newGoal,
          description: newDescription,
          category: newCategory,
          targetDate: newTargetDate,
          progress: 0,
          status: 'active'
        })
      });

      if (response.ok) {
        const newGoalData = await response.json();
        setGoals([newGoalData, ...goals]);
        setNewGoal('');
        setNewDescription('');
        setNewCategory('health');
        setNewTargetDate('');
        setMessage('‚úì Goal added successfully');
        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage('Failed to add goal');
      }
    } catch (err) {
      console.error('Error adding goal:', err);
      setMessage('Error adding goal');
    }
  };

  const updateProgress = async (goalId, progress) => {
    try {
      const response = await fetch(`${API_URL}/api/goals/${goalId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${authToken}`
        },
        body: JSON.stringify({ progress: Math.min(100, Math.max(0, progress)) })
      });

      if (response.ok) {
        const updatedGoal = await response.json();
        setGoals(goals.map(g => g._id === goalId ? updatedGoal : g));
      }
    } catch (err) {
      console.error('Error updating goal:', err);
    }
  };

  const updateStatus = async (goalId, status) => {
    try {
      const response = await fetch(`${API_URL}/api/goals/${goalId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${authToken}`
        },
        body: JSON.stringify({ status })
      });

      if (response.ok) {
        const updatedGoal = await response.json();
        setGoals(goals.map(g => g._id === goalId ? updatedGoal : g));
      }
    } catch (err) {
      console.error('Error updating status:', err);
    }
  };

  const deleteGoal = async (goalId) => {
    try {
      const response = await fetch(`${API_URL}/api/goals/${goalId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${authToken}`
        }
      });

      if (response.ok) {
        setGoals(goals.filter(g => g._id !== goalId));
        setMessage('‚úì Goal deleted');
        setTimeout(() => setMessage(''), 3000);
      }
    } catch (err) {
      console.error('Error deleting goal:', err);
    }
  };



  if (loading) {
    return <div className="goals-container"><h1>Goals & Habits</h1><p>Loading...</p></div>;
  }

  return (
    <div className="goals-container">
      <h1>üéØ Goals & Habits</h1>
      
      {message && <div className="message" style={{
        padding: '10px',
        marginBottom: '15px',
        borderRadius: '5px',
        backgroundColor: message.includes('‚úì') ? '#d4edda' : '#f8d7da',
        color: message.includes('‚úì') ? '#155724' : '#721c24',
        border: `1px solid ${message.includes('‚úì') ? '#c3e6cb' : '#f5c6cb'}`
      }}>
        {message}
      </div>}

      <div className="goals-section">
        <h2>Add New Goal</h2>
        <div className="add-goal-form">
          <input
            type="text"
            value={newGoal}
            onChange={(e) => setNewGoal(e.target.value)}
            placeholder="Goal title..."
            onKeyPress={(e) => e.key === 'Enter' && addGoal()}
          />
          <input
            type="text"
            value={newDescription}
            onChange={(e) => setNewDescription(e.target.value)}
            placeholder="Description (optional)"
          />
          <select value={newCategory} onChange={(e) => setNewCategory(e.target.value)}>
            <option value="health">üèÉ Health</option>
            <option value="learning">üìö Learning</option>
            <option value="career">üíº Career</option>
            <option value="personal">‚ú® Personal</option>
            <option value="finance">üí∞ Finance</option>
          </select>
          <input
            type="date"
            value={newTargetDate}
            onChange={(e) => setNewTargetDate(e.target.value)}
          />
          <button onClick={addGoal} className="btn-add">Add Goal</button>
        </div>
      </div>

      <div className="goals-section">
        <h2>Your Goals ({goals.length})</h2>
        {goals.length === 0 ? (
          <p style={{textAlign: 'center', color: '#666'}}>No goals yet. Create one to get started! üöÄ</p>
        ) : (
          <div className="goals-list">
            {goals.map(goal => (
              <div key={goal._id} className="goal-item">
                <div className="goal-header">
                  <h3>{goal.title}</h3>
                  <span className={`status-badge ${goal.status}`}>{goal.status}</span>
                </div>
                {goal.description && <p className="goal-description">{goal.description}</p>}
                <div className="goal-meta">
                  <span className="category">{goal.category}</span>
                  {goal.targetDate && <span className="target-date">üìÖ {new Date(goal.targetDate).toLocaleDateString()}</span>}
                </div>
                <div className="progress-section">
                  <div className="progress-bar">
                    <div className="progress-fill" style={{width: `${goal.progress}%`}}></div>
                  </div>
                  <div className="progress-controls">
                    <input
                      type="number"
                      min="0"
                      max="100"
                      value={goal.progress}
                      onChange={(e) => updateProgress(goal._id, parseInt(e.target.value))}
                      className="progress-input"
                    />
                    <span className="progress-label">{goal.progress}%</span>
                  </div>
                </div>
                <div className="goal-actions">
                  {goal.status === 'active' && (
                    <button onClick={() => updateStatus(goal._id, 'completed')} className="btn-complete">Complete</button>
                  )}
                  {goal.status !== 'active' && (
                    <button onClick={() => updateStatus(goal._id, 'active')} className="btn-reactivate">Reactivate</button>
                  )}
                  <button onClick={() => deleteGoal(goal._id)} className="btn-delete">Delete</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Goals;
