import React, { useEffect, useMemo, useState } from "react";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

const getMoodEmoji = (mood) => {
  const map = {
    positive: "ðŸ˜Š",
    negative: "ðŸ˜”",
    neutral: "ðŸ˜",
    happy: "ðŸ˜„",
    sad: "ðŸ˜¢",
    angry: "ðŸ˜ ",
    excited: "ðŸ¤©",
    calm: "ðŸ˜Œ",
  };
  return map[mood] || "ðŸ“";
};

export default function Entries() {
  const [entries, setEntries] = useState([]);
  const [query, setQuery] = useState("");
  const [moodFilter, setMoodFilter] = useState("all");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (!userId) return setLoading(false);

    fetch(`${API_URL}/api/entries/user/${userId}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("authToken")}`,
      },
    })
      .then((r) => r.json())
      .then((d) => setEntries(Array.isArray(d) ? d : d.entries))
      .finally(() => setLoading(false));
  }, []);

  const filtered = useMemo(() => {
    return entries.filter((e) => {
      if (moodFilter !== "all" && e.mood !== moodFilter) return false;
      if (!query) return true;
      return (
        e.title?.toLowerCase().includes(query.toLowerCase()) ||
        e.content?.toLowerCase().includes(query.toLowerCase())
      );
    });
  }, [entries, query, moodFilter]);

  if (loading) {
    return (
      <div style={styles.loading}>
        <div style={styles.pulse}>ðŸ“”</div>
        <p>Loading your thoughtsâ€¦</p>
      </div>
    );
  }

  return (
    <div style={styles.page}>
      <h1 style={styles.heading}>Your Journal</h1>

      <input
        style={styles.search}
        placeholder="Search your thoughts..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />

      <div style={styles.filters}>
        {["all", ...new Set(entries.map((e) => e.mood))].map((m) => (
          <button
            key={m}
            onClick={() => setMoodFilter(m)}
            style={{
              ...styles.filter,
              ...(moodFilter === m ? styles.filterActive : {}),
            }}
          >
            {m === "all" ? "All" : `${getMoodEmoji(m)} ${m}`}
          </button>
        ))}
      </div>

      <div style={styles.grid}>
        {filtered.map((e) => (
          <div key={e._id} style={styles.card}>
            <div style={styles.cardTop}>
              <span style={styles.emoji}>{getMoodEmoji(e.mood)}</span>
              <span style={styles.date}>
                {new Date(e.createdAt).toDateString()}
              </span>
            </div>

            {e.title && <h3 style={styles.title}>{e.title}</h3>}

            <p style={styles.text}>
              {e.content.length > 140
                ? e.content.slice(0, 140) + "â€¦"
                : e.content}
            </p>

            <div style={styles.tags}>
              {e.tags?.slice(0, 3).map((t, i) => (
                <span key={i} style={styles.tag}>
                  #{t}
                </span>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

/* ðŸŒˆ Soft pastel styles */
const styles = {
  page: {
    minHeight: "100vh",
    padding: "32px",
    fontFamily: "Inter, system-ui",
    background:
      "linear-gradient(180deg, #fdfbfb 0%, #ebedee 100%)",
  },
  heading: {
    fontSize: "34px",
    fontWeight: 600,
    color: "#374151",
    marginBottom: "24px",
  },
  search: {
    width: "100%",
    padding: "14px",
    borderRadius: "14px",
    border: "none",
    outline: "none",
    fontSize: "15px",
    background: "#ffffffcc",
    boxShadow: "0 6px 20px rgba(0,0,0,0.08)",
    marginBottom: "20px",
  },
  filters: {
    display: "flex",
    gap: "10px",
    flexWrap: "wrap",
    marginBottom: "30px",
  },
  filter: {
    padding: "8px 16px",
    borderRadius: "999px",
    border: "none",
    background: "#e5e7eb",
    cursor: "pointer",
    transition: "all .25s",
  },
  filterActive: {
    background: "#6366f1",
    color: "white",
    transform: "scale(1.05)",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
    gap: "24px",
  },
  card: {
    background:
      "linear-gradient(160deg, #ffffff 0%, #f3f4f6 100%)",
    padding: "22px",
    borderRadius: "22px",
    boxShadow: "0 20px 40px rgba(0,0,0,0.12)",
    transition: "transform .3s",
  },
  cardTop: {
    display: "flex",
    justifyContent: "space-between",
    marginBottom: "10px",
  },
  emoji: {
    fontSize: "24px",
  },
  date: {
    fontSize: "12px",
    color: "#6b7280",
  },
  title: {
    fontSize: "18px",
    fontWeight: 600,
    margin: "10px 0",
    color: "#111827",
  },
  text: {
    fontSize: "14px",
    lineHeight: 1.7,
    color: "#374151",
  },
  tags: {
    marginTop: "14px",
    display: "flex",
    gap: "6px",
    flexWrap: "wrap",
  },
  tag: {
    background: "#eef2ff",
    color: "#4338ca",
    padding: "4px 12px",
    borderRadius: "999px",
    fontSize: "12px",
  },
  loading: {
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    background:
      "linear-gradient(180deg, #fdfbfb 0%, #ebedee 100%)",
  },
  pulse: {
    fontSize: "48px",
    animation: "pulse 1.5s infinite",
  },
};
