import React, { useMemo, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Signup.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001';

export default function Signup() {
  const navigate = useNavigate();
  const [form, setForm] = useState({ username: '', email: '', password: '', confirm: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPwd, setShowPwd] = useState(false);

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const strength = useMemo(() => {
    const p = form.password || '';
    let score = 0;
    if (p.length >= 6) score += 1;
    if (p.length >= 10) score += 1;
    if (/[A-Z]/.test(p)) score += 1;
    if (/[0-9]/.test(p)) score += 1;
    if (/[^A-Za-z0-9]/.test(p)) score += 1;
    const pct = Math.min(100, (score / 5) * 100);
    const label = pct < 40 ? 'Weak' : pct < 70 ? 'Medium' : 'Strong';
    return { pct, label };
  }, [form.password]);

  const handleSignup = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const username = form.username.trim();
    const email = form.email.trim();
    const password = form.password;
    const confirm = form.confirm;

    if (!username || !email || !password || !confirm) {
      setError('Please fill all fields.');
      setLoading(false);
      return;
    }
    
    // Basic email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError('Please enter a valid email address.');
      setLoading(false);
      return;
    }
    
    if (password.length < 6) {
      setError('Password should be at least 6 characters.');
      setLoading(false);
      return;
    }
    if (password !== confirm) {
      setError('Passwords do not match.');
      setLoading(false);
      return;
    }

    try {
      const response = await fetch(`${API_URL}/api/auth/signup`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, email, password })
      });

      const data = await response.json();
      
      if (!response.ok) {
        setError(data.error || 'Signup failed');
        setLoading(false);
        return;
      }

      // Store token and user info
      localStorage.setItem('authToken', data.token);
      localStorage.setItem('userId', data.user.id);
      localStorage.setItem('username', data.user.username);
      localStorage.setItem('user', data.user.username);
      
      navigate('/journal');
    } catch (err) {
      setError('Error: Unable to connect to server. Make sure backend is running on port 5001.');
      console.error('Signup error:', err);
    }
    setLoading(false);
  };

  return (
    <div className="signup-page">
      <div className="bg-orb orb1" />
      <div className="bg-orb orb2" />
      <div className="card fade-in">
        <div className="card-head">
          <h2>Create your account</h2>
          <p className="muted">Start journaling with privacy-first storage.</p>
        </div>
        <form onSubmit={handleSignup} className="form">
          <label className="field">
            <span>Username</span>
            <input
              type="text"
              name="username"
              placeholder="e.g. alex"
              autoComplete="username"
              value={form.username}
              onChange={onChange}
              required
            />
          </label>
          <label className="field">
            <span>Email</span>
            <input
              type="email"
              name="email"
              placeholder="e.g. alex@example.com"
              autoComplete="email"
              value={form.email}
              onChange={onChange}
              required
            />
          </label>
          <label className="field">
            <span>Password</span>
            <div className="password-wrap">
              <input
                type={showPwd ? 'text' : 'password'}
                name="password"
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                autoComplete="new-password"
                value={form.password}
                onChange={onChange}
                required
              />
              <button type="button" className="eye" onClick={() => setShowPwd((s) => !s)} aria-label="Toggle password visibility">
                {showPwd ? 'üôà' : 'üëÅÔ∏è'}
              </button>
            </div>
            <div className="strength">
              <div className="bar" style={{ width: `${strength.pct}%` }} />
              <span className={`label ${strength.label.toLowerCase()}`}>{strength.label}</span>
            </div>
          </label>
          <label className="field">
            <span>Confirm password</span>
            <input
              type="password"
              name="confirm"
              placeholder="Repeat password"
              autoComplete="new-password"
              value={form.confirm}
              onChange={onChange}
              required
            />
          </label>
          {error && <div className="error">{error}</div>}
          <button type="submit" className="btn-cta">Create account</button>
        </form>
        <p className="foot">
          Already have an account? <Link to="/auth">Log in</Link>
        </p>
      </div>
    </div>
  );
}
