import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Journaling.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001';

const Journaling = () => {
  const [entry, setEntry] = useState('');
  const [title, setTitle] = useState('');
  const [mood, setMood] = useState('neutral');
  const [files, setFiles] = useState([]);
  const [images, setImages] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [transcription, setTranscription] = useState('');
  const [saving, setSaving] = useState(false);
  const [savedMsg, setSavedMsg] = useState('');
  const [error, setError] = useState('');
  const recognitionRef = useRef(null);
  const navigate = useNavigate();

  const moods = [
    { emoji: 'üòä', label: 'happy', color: '#fbbf24' },
    { emoji: 'üòî', label: 'sad', color: '#f87171' },
    { emoji: 'üòå', label: 'calm', color: '#60a5fa' },
    { emoji: 'üò†', label: 'angry', color: '#fb7185' },
    { emoji: 'üò¥', label: 'tired', color: '#a78bfa' },
    { emoji: 'üòä', label: 'neutral', color: '#9ca3af' }
  ];

  const handleFileUpload = (e) => {
    const uploadedFiles = Array.from(e.target.files || []);
    const newImages = uploadedFiles.filter(f => f.type.startsWith('image/'));
    const newFiles = uploadedFiles.filter(f => !f.type.startsWith('image/'));
    
    setImages([...images, ...newImages]);
    setFiles([...files, ...newFiles]);
  };

  const removeImage = (idx) => {
    setImages(images.filter((_, i) => i !== idx));
  };

  const removeFile = (idx) => {
    setFiles(files.filter((_, i) => i !== idx));
  };

  const startRecording = () => {
    if (!('webkitSpeechRecognition' in window)) {
      alert('Speech recognition not supported');
      return;
    }
    // eslint-disable-next-line no-undef
    const recognition = new webkitSpeechRecognition();
    recognition.continuous = false;
    recognition.interimResults = false;
    recognition.lang = 'en-US';

    recognition.onstart = () => setIsRecording(true);
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setTranscription(transcript);
      setEntry(prev => prev + ' ' + transcript);
    };
    recognition.onend = () => setIsRecording(false);

    recognitionRef.current = recognition;
    recognition.start();
  };

  const stopRecording = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
  };

  const saveEntry = async () => {
    const userId = localStorage.getItem('userId');
    const user = localStorage.getItem('user');
    
    if (!userId || !user) {
      setError('Please log in first');
      return;
    }

    if (!entry.trim()) {
      setError('Please write something before saving');
      return;
    }

    setSaving(true);
    setError('');

    try {
      // Convert images and files to data URLs for storage
      const imageObjs = await Promise.all(
        images.map(
          (f) =>
            new Promise((resolve) => {
              const reader = new FileReader();
              reader.onload = () =>
                resolve({
                  name: f.name,
                  type: f.type,
                  size: f.size,
                  dataUrl: reader.result,
                });
              reader.readAsDataURL(f);
            })
        )
      );

      const fileObjs = await Promise.all(
        files.map(
          (f) =>
            new Promise((resolve) => {
              const reader = new FileReader();
              reader.onload = () =>
                resolve({
                  name: f.name,
                  type: f.type,
                  size: f.size,
                  dataUrl: reader.result,
                });
              reader.readAsDataURL(f);
            })
        )
      );

      const entryData = {
        userId,
        title: title || 'Untitled Entry',
        content: entry,
        images: imageObjs,
        files: fileObjs,
        tags: generateTags(entry),
        mood: mood
        // sentiment will be analyzed by backend
      };

      // Send to backend API
      const response = await fetch(`${API_URL}/api/entries`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`
        },
        body: JSON.stringify(entryData)
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.error || 'Failed to save entry');
      }

      // Reset form
      setEntry('');
      setTitle('');
      setMood('neutral');
      setFiles([]);
      setImages([]);
      setTranscription('');
      setSaving(false);
      setSavedMsg('‚úì Entry saved successfully!');
      setTimeout(() => setSavedMsg(''), 2000);
      // Navigate to entries page to view the saved item
      setTimeout(() => navigate('/entries'), 500);
    } catch (err) {
      setError(err.message || 'Error saving entry. Make sure backend is running.');
      console.error('Save entry error:', err);
      setSaving(false);
    }
  };

  const generateTags = (text) => {
    // Mock keyword extraction
    const words = text.toLowerCase().split(/\s+/);
    return words.filter(w => w.length > 3).slice(0, 5);
  };

  return (
    <div className="journaling-page">
      <h1>‚ú® New Journal Entry</h1>

      {/* Entry Form Section */}
      <div className="journaling-section">
        <h2>Write Your Thoughts</h2>
        
        {error && (
          <div className="error-message" style={{marginBottom:'16px'}}>
            {error}
          </div>
        )}
        {savedMsg && (
          <div className="success-message" style={{marginBottom:'16px'}}>
            {savedMsg}
          </div>
        )}

        <div className="entry-form">
          {/* Title Input */}
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Give your entry a title (optional)"
            className="entry-title-input"
          />

          {/* Mood Selector */}
          <div className="mood-selector">
            <label>How are you feeling today?</label>
            <div className="mood-options">
              {moods.map((m) => (
                <button
                  key={m.label}
                  className={`mood-btn ${mood === m.label ? 'active' : ''}`}
                  onClick={() => setMood(m.label)}
                  style={{
                    borderColor: mood === m.label ? m.color : 'rgba(255, 255, 255, 0.1)'
                  }}
                >
                  <span className="mood-emoji">{m.emoji}</span>
                  <span className="mood-label">{m.label}</span>
                </button>
              ))}
            </div>
          </div>

          {/* Main Text Area */}
          <textarea
            value={entry}
            onChange={(e) => setEntry(e.target.value)}
            placeholder="Write your thoughts, feelings, and experiences here..."
            className="entry-textarea"
          />

          {/* Image Upload */}
          <div className="upload-section">
            <label className="upload-label">
              üì∏ Add Images
              <input 
                type="file" 
                multiple 
                onChange={handleFileUpload} 
                accept="image/*"
                style={{ display: 'none' }}
              />
            </label>
            {images.length > 0 && (
              <div className="media-grid">
                {images.map((img, i) => (
                  <div key={i} className="media-item">
                    <img 
                      src={URL.createObjectURL(img)} 
                      alt={img.name}
                      style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                    />
                    <button 
                      className="remove-media" 
                      onClick={() => removeImage(i)}
                    >
                      ‚úï
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* File Upload */}
          <div className="upload-section">
            <label className="upload-label">
              üìé Attach Files
              <input 
                type="file" 
                multiple 
                onChange={handleFileUpload} 
                accept=".pdf,.doc,.docx,.txt,video/*"
                style={{ display: 'none' }}
              />
            </label>
            {files.length > 0 && (
              <div className="file-list">
                {files.map((f, i) => (
                  <div key={i} className="file-item">
                    <span className="file-name">{f.name}</span>
                    <button 
                      className="remove-file" 
                      onClick={() => removeFile(i)}
                    >
                      ‚úï
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Voice Recording */}
          <div className="voice-section">
            <button 
              className={`btn-voice ${isRecording ? 'recording' : ''}`}
              onClick={isRecording ? stopRecording : startRecording}
            >
              <span className="voice-icon">{isRecording ? '‚èπÔ∏è' : 'üé§'}</span>
              {isRecording ? 'Stop Recording' : 'Start Voice Recording'}
            </button>
            {transcription && (
              <div className="transcription-preview">
                <p className="transcription-label">Transcribed:</p>
                <p className="transcription-text">{transcription}</p>
              </div>
            )}
          </div>

          {/* Action Buttons */}
          <div className="action-buttons">
            <button 
              className="btn-save" 
              onClick={saveEntry} 
              disabled={saving || !entry.trim()}
            >
              {saving ? 'üíæ Saving...' : 'üíæ Save Entry'}
            </button>
            <button 
              className="btn-clear"
              onClick={() => {
                setEntry('');
                setTitle('');
                setMood('neutral');
                setFiles([]);
                setImages([]);
                setTranscription('');
                setError('');
              }}
            >
              Clear
            </button>
          </div>
        </div>
      </div>

      {/* Tips Section */}
      <div className="tips-section">
        <h3>‚ú® Journaling Tips</h3>
        <ul>
          <li>Write freely without worrying about grammar or structure</li>
          <li>Express your genuine emotions and thoughts</li>
          <li>Add images or voice notes to enhance your entries</li>
          <li>Use tags to categorize and organize your thoughts</li>
          <li>Review past entries to track your personal growth</li>
        </ul>
      </div>
    </div>
  );
};

export default Journaling;
