import React, { useState, useRef, useEffect } from 'react';
import './Sage.css';

const SageAssistant = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      sender: 'sage',
      text: "Hello, I'm MemoTalks. I'm here to listen, support, and guide you through your thoughts and feelings. Share what's on your mind."
    }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const motivationalResponses = {
    sad: [
      "It's okay to feel down. üíô Remember, difficult moments are just chapters, not the whole story. You've overcome challenges before, and you have the strength to do it again.",
      "Your sadness shows you care deeply. That's a beautiful quality. üå± Take a moment to breathe, and know that this feeling will pass.",
      "Even on the darkest days, there's a glimmer of hope. You're stronger than you think. Would you like to talk about what's bothering you?",
      "Sadness is temporary, but your resilience is permanent. üí™ Let's find one small thing to be grateful for today.",
      "It's brave of you to acknowledge your feelings. That first step of self-awareness is powerful. You're not alone in this. ü§ù"
    ],
    happy: [
      "Your joy is contagious! üéâ Celebrate this moment - you deserve it! Keep this feeling alive and share it with others.",
      "This is amazing! Your happiness is a testament to your efforts. Keep riding this wave of positivity! üåä‚ú®",
      "You're shining bright! üåü This is what success looks like. Be proud of yourself and savor this victory!",
      "What a wonderful time to be alive! Your happiness deserves to be celebrated. Keep this momentum going! üöÄ",
      "You're glowing! üí´ Remember this feeling on difficult days. You've just proven you can achieve great things!"
    ],
    neutral: [
      "Every moment is an opportunity for growth. üå± What would you like to focus on today?",
      "I'm here to listen and support you. What's occupying your thoughts right now?",
      "Life is a beautiful journey of ups and downs. You're exactly where you need to be. üõ§Ô∏è",
      "Curiosity and reflection are keys to growth. What questions can I help you explore?",
      "You're taking steps to understand yourself better. That's wonderful! What's your next step?"
    ],
    doubt: [
      "Doubt is the mind's way of protecting you, but it can also hold you back. üîì Let's work through this together. What specifically are you doubting?",
      "Every successful person has faced doubt. The difference? They didn't let it stop them. üí™ What's holding you back?",
      "Uncertainty is natural, but you're stronger than you think. Let's break down your concerns and find clarity. üéØ",
      "Doubt often comes from fear of the unknown. But remember, you've faced unknowns before. What questions do you have?",
      "I sense some hesitation. That's okay! Let's explore what's causing this doubt and find ways to build your confidence. üåü"
    ],
    motivation: [
      "You're capable of incredible things! üöÄ Focus on your goals and take one step at a time. Progress > perfection!",
      "Your dreams are worth pursuing. Every small action brings you closer to your goals. Keep going! üéØ‚ú®",
      "Believe in yourself like you believe in others. You have untapped potential waiting to be unleashed. üí™üî•",
      "The only person who can stop you is you. So don't! Move forward with confidence. üåü",
      "Your future self will thank you for the effort you're putting in today. Keep pushing! üôå"
    ]
  };

  const generateResponse = (userMessage) => {
    const lowerMessage = userMessage.toLowerCase();
    let category = 'neutral';
    let responses = motivationalResponses.neutral;

    // Detect emotional context
    if (lowerMessage.match(/sad|depressed|down|lonely|miserable|terrible|awful|hurts|pain|cry|broken|lost/)) {
      category = 'sad';
      responses = motivationalResponses.sad;
    } else if (lowerMessage.match(/happy|great|amazing|wonderful|excited|proud|love|celebrate|success|win|achieved/)) {
      category = 'happy';
      responses = motivationalResponses.happy;
    } else if (lowerMessage.match(/doubt|unsure|uncertain|not sure|confused|unclear|help me|what if|worried|anxious|scared|afraid|can i|should i|will i/)) {
      category = 'doubt';
      responses = motivationalResponses.doubt;
    } else if (lowerMessage.match(/motivate|inspire|encourage|push me|help me/)) {
      category = 'motivation';
      responses = motivationalResponses.motivation;
    }

    // Return random response from category
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = {
      id: Date.now(),
      sender: 'user',
      text: input
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setLoading(true);

    // Simulate thinking delay
    setTimeout(() => {
      const sageResponse = {
        id: Date.now() + 1,
        sender: 'sage',
        text: generateResponse(input)
      };
      setMessages(prev => [...prev, sageResponse]);
      setLoading(false);
    }, 800);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const quickPrompts = [
    { emoji: 'üò¢', text: 'I\'m feeling down', message: 'I\'m feeling down today and need support' },
    { emoji: 'üéâ', text: 'I achieved something', message: 'I\'m happy! I achieved something great today' },
    { emoji: '‚ùì', text: 'I have doubts', message: 'I have doubts about my decisions' },
    { emoji: 'üöÄ', text: 'Motivate me', message: 'I need motivation to keep going' }
  ];

  return (
    <div className="main-content">
      <div className="sage-container">
        <div className="sage-header">
          <div className="sage-title">
            <span className="sage-emoji">üí≠</span>
            <div>
              <h1>MemoTalks</h1>
              <p className="sage-subtitle">Your Personal Motivational Companion</p>
            </div>
          </div>
          <p className="sage-intro">Welcome to your safe space. Share your thoughts, feelings, and doubts. I'm here to support, motivate, and guide you through every moment.</p>
        </div>

        <div className="messages-container">
          {messages.length === 1 && (
            <div className="quick-prompts">
              <p className="prompts-label">Quick Start</p>
              <div className="prompts-grid">
                {quickPrompts.map((prompt, idx) => (
                  <button
                    key={idx}
                    className="prompt-button"
                    onClick={() => {
                      setInput(prompt.message);
                    }}
                  >
                    <span className="prompt-emoji">{prompt.emoji}</span>
                    <span className="prompt-text">{prompt.text}</span>
                  </button>
                ))}
              </div>
            </div>
          )}
          
          <div className="messages">
            {messages.map((msg) => (
              <div key={msg.id} className={`message ${msg.sender}`}>
                <div className="message-content">
                  {msg.sender === 'sage' && <span className="sage-icon">üí≠</span>}
                  <p>{msg.text}</p>
                </div>
              </div>
            ))}
            {loading && (
              <div className="message sage">
                <div className="message-content">
                  <span className="sage-icon">üí≠</span>
                  <div className="typing-indicator">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>
        </div>

        <div className="input-container">
          <div className="input-wrapper">
            <textarea
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Share your thoughts, feelings, or ask me anything... (Press Enter to send, Shift+Enter for new line)"
              className="message-input"
              rows="3"
            />
            <button
              onClick={handleSend}
              disabled={!input.trim() || loading}
              className="send-button"
            >
              {loading ? '...' : '‚úàÔ∏è'}
            </button>
          </div>
          <p className="input-hint">üí° Share anything - your joy, concerns, doubts, or goals. I'm here to listen and support you.</p>
        </div>
      </div>
    </div>
  );
};

export default SageAssistant;
