import React, { useEffect, useMemo, useState } from 'react';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const getMoodEmoji = (mood) => {
  const moodMap = {
    positive: 'ğŸ˜Š',
    negative: 'ğŸ˜”',
    neutral: 'ğŸ˜',
    happy: 'ğŸ˜„',
    sad: 'ğŸ˜¢',
    angry: 'ğŸ˜ ',
    excited: 'ğŸ¤©',
    calm: 'ğŸ˜Œ'
  };
  return moodMap[mood] || 'ğŸ“';
};

const getSentimentColor = (sentiment) => {
  const colors = {
    positive: '#10b981',
    negative: '#ef4444',
    neutral: '#6b7280'
  };
  return colors[sentiment] || '#6b7280';
};

const StatCard = ({ icon, value, label, gradient }) => (
  <div style={{ 
    background: gradient,
    padding: '12px 16px',
    borderRadius: '12px',
    color: 'white',
    textAlign: 'center',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
    border: '1px solid rgba(255, 255, 255, 0.1)',
    transition: 'all 0.3s ease',
    cursor: 'default',
    position: 'relative',
    overflow: 'hidden'
  }}
  onMouseEnter={(el) => {
    el.currentTarget.style.transform = 'translateY(-4px)';
    el.currentTarget.style.boxShadow = '0 8px 20px rgba(0, 0, 0, 0.2)';
  }}
  onMouseLeave={(el) => {
    el.currentTarget.style.transform = 'translateY(0)';
    el.currentTarget.style.boxShadow = '0 4px 12px rgba(0, 0, 0, 0.15)';
  }}>
    <div style={{
      position: 'absolute',
      top: '-50%',
      right: '-20%',
      width: '100px',
      height: '100px',
      background: 'rgba(255, 255, 255, 0.05)',
      borderRadius: '50%',
      animation: 'float 6s ease-in-out infinite'
    }} />
    <div style={{ position: 'relative', zIndex: 1 }}>
      <div style={{ fontSize: '14px', marginBottom: '4px' }}>{icon}</div>
      <div style={{ fontSize: '22px', fontWeight: '800', marginBottom: '2px' }}>{value}</div>
      <div style={{ fontSize: '11px', opacity: 0.85, fontWeight: '500', letterSpacing: '0.3px' }}>{label}</div>
    </div>
  </div>
);

export default function Entries() {
  const [entries, setEntries] = useState([]);
  const [query, setQuery] = useState('');
  const [moodFilter, setMoodFilter] = useState('all');
  const [loading, setLoading] = useState(true);
  const [selectedEntry, setSelectedEntry] = useState(null);
  const [streak, setStreak] = useState({ current: 0, longest: 0, lastEntryDate: null });

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (!userId) {
      setLoading(false);
      return;
    }

    const fetchEntries = async () => {
      try {
        const response = await fetch(`${API_URL}/api/entries/user/${userId}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('authToken')}`
          }
        });

        if (response.ok) {
          const data = await response.json();
          if (Array.isArray(data)) {
            setEntries(data);
          } else {
            setEntries(data.entries);
            setStreak(data.streak);
          }
        } else {
          console.error('Failed to fetch entries');
        }
      } catch (err) {
        console.error('Error fetching entries:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchEntries();
  }, []);

  const filtered = useMemo(() => {
    let result = entries;
    
    if (moodFilter !== 'all') {
      result = result.filter(e => e.mood === moodFilter);
    }

    const q = query.trim().toLowerCase();
    if (q) {
      result = result.filter((e) =>
        (e.content || '').toLowerCase().includes(q) ||
        (e.title || '').toLowerCase().includes(q) ||
        (e.tags || []).some((t) => t.toLowerCase().includes(q))
      );
    }
    
    return result;
  }, [entries, query, moodFilter]);

  const stats = {
    total: entries.length,
    thisMonth: entries.filter(e => {
      const date = new Date(e.createdAt);
      const now = new Date();
      return date.getMonth() === now.getMonth() && date.getFullYear() === now.getFullYear();
    }).length,
    moods: [...new Set(entries.map(e => e.mood))]
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);
    
    if (date.toDateString() === today.toDateString()) return 'Today';
    if (date.toDateString() === yesterday.toDateString()) return 'Yesterday';
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' });
  };

  const getContentPreview = (content) => {
    return content.length > 160 ? content.substring(0, 160) + '...' : content;
  };

  if (loading) {
    return (
      <div className="main-content">
        <div style={{ textAlign: 'center', padding: '80px 20px', minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div>
            <div style={{ 
              fontSize: '56px', 
              marginBottom: '24px',
              animation: 'bounce 2s infinite'
            }}>ğŸ“”</div>
            <p style={{ fontSize: '18px', color: '#9ca3af', fontWeight: '500' }}>Loading your memories...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="main-content">
      <div style={{ maxWidth: '1400px', margin: '0 auto', padding: '40px 24px' }}>
        {/* Header with Gradient Background */}
        <div style={{
          background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(139, 92, 246, 0.05) 100%)',
          borderRadius: '24px',
          padding: '40px 32px',
          marginBottom: '40px',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(10px)'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '16px', marginBottom: '28px' }}>
            <div style={{ fontSize: '48px', animation: 'pulse 3s ease-in-out infinite' }}>ğŸ“”</div>
            <div>
              <h1 style={{ margin: 0, fontSize: '40px', fontWeight: '800', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent' }}>
                Your Journal Entries
              </h1>
              <p style={{ margin: '8px 0 0 0', fontSize: '14px', color: '#9ca3af', fontWeight: '500' }}>
                {entries.length} {entries.length === 1 ? 'entry' : 'entries'} logged â€¢ Keep your streak going! ğŸ”¥
              </p>
            </div>
          </div>
          
          {/* Stats Grid */}
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(160px, 1fr))', 
            gap: '16px'
          }}>
            <StatCard 
              icon="ğŸ“Š" 
              value={stats.total} 
              label="Total Entries" 
              gradient="linear-gradient(135deg, #667eea 0%, #764ba2 100%)"
            />
            <StatCard 
              icon="ğŸ“…" 
              value={stats.thisMonth} 
              label="This Month" 
              gradient="linear-gradient(135deg, #f093fb 0%, #f5576c 100%)"
            />
            <StatCard 
              icon="ğŸ­" 
              value={stats.moods.length} 
              label="Mood Types" 
              gradient="linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)"
            />
            <StatCard 
              icon="ğŸ”¥" 
              value={streak.current} 
              label="Current Streak" 
              gradient="linear-gradient(135deg, #fa709a 0%, #fee140 100%)"
            />
            <StatCard 
              icon="â­" 
              value={streak.longest} 
              label="Best Streak" 
              gradient="linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)"
            />
          </div>
        </div>

        {/* Search and Filter Section */}
        <div style={{ 
          background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.08) 0%, rgba(255, 255, 255, 0.02) 100%)',
          padding: '28px',
          borderRadius: '20px',
          marginBottom: '40px',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)'
        }}>
          {/* Search Input */}
          <div style={{ marginBottom: '24px' }}>
            <label style={{ display: 'block', fontSize: '13px', fontWeight: '600', color: '#9ca3af', marginBottom: '10px', textTransform: 'uppercase', letterSpacing: '0.8px' }}>
              ğŸ” Search Entries
            </label>
            <input
              type="text"
              placeholder="Find by title, content, or tags..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              style={{
                width: '100%',
                padding: '14px 18px',
                borderRadius: '12px',
                border: '2px solid transparent',
                background: 'rgba(255, 255, 255, 0.08)',
                color: 'white',
                fontSize: '15px',
                fontWeight: '500',
                boxSizing: 'border-box',
                transition: 'all 0.3s ease',
                outline: 'none'
              }}
              onFocus={(e) => {
                e.currentTarget.style.background = 'rgba(102, 126, 234, 0.15)';
                e.currentTarget.style.borderColor = 'rgba(102, 126, 234, 0.5)';
              }}
              onBlur={(e) => {
                e.currentTarget.style.background = 'rgba(255, 255, 255, 0.08)';
                e.currentTarget.style.borderColor = 'transparent';
              }}
            />
          </div>

          {/* Mood Filter */}
          <div>
            <label style={{ display: 'block', fontSize: '13px', fontWeight: '600', color: '#9ca3af', marginBottom: '12px', textTransform: 'uppercase', letterSpacing: '0.8px' }}>
              ğŸ­ Filter by Mood
            </label>
            <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
              <button
                onClick={() => setMoodFilter('all')}
                style={{
                  padding: '10px 20px',
                  borderRadius: '10px',
                  border: moodFilter === 'all' ? '2px solid #667eea' : '2px solid transparent',
                  background: moodFilter === 'all' ? 'rgba(102, 126, 234, 0.2)' : 'rgba(255, 255, 255, 0.05)',
                  color: moodFilter === 'all' ? '#a5b4fc' : '#d1d5db',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: moodFilter === 'all' ? '700' : '500',
                  transition: 'all 0.3s ease'
                }}
                onMouseEnter={(e) => {
                  if (moodFilter !== 'all') {
                    e.currentTarget.style.background = 'rgba(255, 255, 255, 0.1)';
                  }
                }}
                onMouseLeave={(e) => {
                  if (moodFilter !== 'all') {
                    e.currentTarget.style.background = 'rgba(255, 255, 255, 0.05)';
                  }
                }}
              >
                All Moods
              </button>
              {stats.moods.map(mood => (
                <button
                  key={mood}
                  onClick={() => setMoodFilter(mood)}
                  style={{
                    padding: '10px 20px',
                    borderRadius: '10px',
                    border: moodFilter === mood ? '2px solid #667eea' : '2px solid transparent',
                    background: moodFilter === mood ? 'rgba(102, 126, 234, 0.2)' : 'rgba(255, 255, 255, 0.05)',
                    color: moodFilter === mood ? '#a5b4fc' : '#d1d5db',
                    cursor: 'pointer',
                    fontSize: '14px',
                    fontWeight: moodFilter === mood ? '700' : '500',
                    transition: 'all 0.3s ease'
                  }}
                  onMouseEnter={(e) => {
                    if (moodFilter !== mood) {
                      e.currentTarget.style.background = 'rgba(255, 255, 255, 0.1)';
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (moodFilter !== mood) {
                      e.currentTarget.style.background = 'rgba(255, 255, 255, 0.05)';
                    }
                  }}
                >
                  {getMoodEmoji(mood)} <span style={{ marginLeft: '6px', textTransform: 'capitalize' }}>{mood}</span>
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Entries Grid */}
        {filtered.length === 0 ? (
          <div style={{ 
            textAlign: 'center', 
            padding: '100px 40px',
            color: '#9ca3af'
          }}>
            <div style={{ fontSize: '72px', marginBottom: '24px', animation: 'bounce 2s infinite' }}>ğŸ”</div>
            <p style={{ fontSize: '20px', marginBottom: '10px', fontWeight: '600', color: '#d1d5db' }}>No entries found</p>
            <p style={{ fontSize: '15px', opacity: 0.7 }}>Try adjusting your search or mood filters</p>
          </div>
        ) : (
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fill, minmax(340px, 1fr))',
            gap: '24px'
          }}>
            {filtered.map((e, idx) => (
              <article 
                key={e._id} 
                style={{
                  background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.08) 0%, rgba(255, 255, 255, 0.03) 100%)',
                  border: '1px solid rgba(255, 255, 255, 0.1)',
                  borderRadius: '18px',
                  padding: '28px',
                  backdropFilter: 'blur(10px)',
                  cursor: 'pointer',
                  transition: 'all 0.4s cubic-bezier(0.34, 1.56, 0.64, 1)',
                  display: 'flex',
                  flexDirection: 'column',
                  height: '100%',
                  boxShadow: '0 8px 24px rgba(0, 0, 0, 0.1)',
                  position: 'relative',
                  overflow: 'hidden',
                  animation: `slideIn 0.5s ease-out ${idx * 0.05}s both`
                }}
                onMouseEnter={(el) => {
                  el.currentTarget.style.transform = 'translateY(-12px)';
                  el.currentTarget.style.boxShadow = '0 20px 40px rgba(0, 0, 0, 0.2)';
                  el.currentTarget.style.borderColor = 'rgba(102, 126, 234, 0.4)';
                }}
                onMouseLeave={(el) => {
                  el.currentTarget.style.transform = 'translateY(0)';
                  el.currentTarget.style.boxShadow = '0 8px 24px rgba(0, 0, 0, 0.1)';
                  el.currentTarget.style.borderColor = 'rgba(255, 255, 255, 0.1)';
                }}
              >
                {/* Decorative Background */}
                <div style={{
                  position: 'absolute',
                  top: '-100px',
                  right: '-100px',
                  width: '250px',
                  height: '250px',
                  background: 'radial-gradient(circle, rgba(102, 126, 234, 0.1) 0%, transparent 70%)',
                  borderRadius: '50%',
                  pointerEvents: 'none'
                }} />

                {/* Entry Header */}
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '16px', position: 'relative', zIndex: 1 }}>
                  <div>
                    <div style={{ fontSize: '36px', marginBottom: '8px' }}>{getMoodEmoji(e.mood)}</div>
                    <small style={{ color: '#9ca3af', fontSize: '12px', fontWeight: '600', letterSpacing: '0.5px' }}>
                      {formatDate(e.createdAt)}
                    </small>
                  </div>
                  <div style={{
                    padding: '8px 14px',
                    background: getSentimentColor(e.sentiment),
                    color: 'white',
                    borderRadius: '8px',
                    fontSize: '11px',
                    fontWeight: '700',
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px',
                    boxShadow: `0 4px 12px ${getSentimentColor(e.sentiment)}40`
                  }}>
                    {e.sentiment || 'neutral'}
                  </div>
                </div>

                {/* Entry Title */}
                {e.title && (
                  <h3 style={{
                    margin: '0 0 12px 0',
                    fontSize: '18px',
                    fontWeight: '700',
                    color: 'white',
                    lineHeight: '1.5',
                    position: 'relative',
                    zIndex: 1
                  }}>
                    {e.title}
                  </h3>
                )}

                {/* Entry Content Preview */}
                <p style={{
                  margin: '0 0 20px 0',
                  fontSize: '14px',
                  lineHeight: '1.7',
                  color: '#d1d5db',
                  flexGrow: 1,
                  position: 'relative',
                  zIndex: 1
                }}>
                  {getContentPreview(e.content)}
                </p>

                {/* Images Grid */}
                {e.images?.length > 0 && (
                  <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(2, 1fr)',
                    gap: '10px',
                    marginBottom: '20px',
                    borderRadius: '12px',
                    overflow: 'hidden',
                    position: 'relative',
                    zIndex: 1
                  }}>
                    {e.images.slice(0, 4).map((img, idx) => (
                      <div key={idx} style={{ position: 'relative', overflow: 'hidden', borderRadius: '10px', background: 'rgba(0, 0, 0, 0.3)' }}>
                        <img 
                          alt={img.name} 
                          src={img.dataUrl} 
                          style={{
                            width: '100%',
                            height: '140px',
                            objectFit: 'cover',
                            transition: 'transform 0.4s ease',
                          }}
                          onMouseEnter={(el) => el.currentTarget.style.transform = 'scale(1.08)'}
                          onMouseLeave={(el) => el.currentTarget.style.transform = 'scale(1)'}
                        />
                        {e.images.length > 4 && idx === 3 && (
                          <div style={{
                            position: 'absolute',
                            inset: 0,
                            background: 'rgba(0, 0, 0, 0.7)',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            color: 'white',
                            fontSize: '20px',
                            fontWeight: '800',
                            backdropFilter: 'blur(4px)'
                          }}>
                            +{e.images.length - 4}
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                )}

                {/* Files */}
                {e.files?.length > 0 && (
                  <div style={{ marginBottom: '18px', position: 'relative', zIndex: 1 }}>
                    <div style={{ 
                      fontSize: '13px', 
                      color: '#9ca3af', 
                      fontWeight: '600',
                      background: 'rgba(102, 126, 234, 0.1)',
                      padding: '10px 12px',
                      borderRadius: '8px',
                      border: '1px solid rgba(102, 126, 234, 0.2)'
                    }}>
                      ğŸ“ {e.files.length} attachment{e.files.length > 1 ? 's' : ''}
                    </div>
                  </div>
                )}

                {/* Tags */}
                {e.tags?.length > 0 && (
                  <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap', marginTop: 'auto', position: 'relative', zIndex: 1 }}>
                    {e.tags.slice(0, 3).map((tag, idx) => (
                      <span 
                        key={idx}
                        style={{
                          padding: '6px 12px',
                          background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.3) 0%, rgba(139, 92, 246, 0.2) 100%)',
                          color: '#a5b4fc',
                          borderRadius: '8px',
                          fontSize: '12px',
                          fontWeight: '600',
                          border: '1px solid rgba(102, 126, 234, 0.3)',
                          transition: 'all 0.3s ease'
                        }}
                        onMouseEnter={(el) => {
                          el.currentTarget.style.background = 'linear-gradient(135deg, rgba(102, 126, 234, 0.5) 0%, rgba(139, 92, 246, 0.4) 100%)';
                          el.currentTarget.style.borderColor = 'rgba(102, 126, 234, 0.6)';
                        }}
                        onMouseLeave={(el) => {
                          el.currentTarget.style.background = 'linear-gradient(135deg, rgba(102, 126, 234, 0.3) 0%, rgba(139, 92, 246, 0.2) 100%)';
                          el.currentTarget.style.borderColor = 'rgba(102, 126, 234, 0.3)';
                        }}
                      >
                        #{tag}
                      </span>
                    ))}
                    {e.tags.length > 3 && (
                      <span style={{
                        padding: '6px 12px',
                        color: '#9ca3af',
                        fontSize: '12px',
                        fontWeight: '600'
                      }}>
                        +{e.tags.length - 3} more
                      </span>
                    )}
                  </div>
                )}
              </article>
            ))}
          </div>
        )}
      </div>

      <style>{`
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }
        @keyframes bounce {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-20px); }
        }
        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-20px); }
        }
        @keyframes pulse {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.7; }
        }
        @keyframes slideIn {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
}
