import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Footer.css';

export default function Footer() {
  const location = useLocation();
  const user = localStorage.getItem('user');

  const navItem = (to, label, emoji) => (
    <li className={location.pathname === to ? 'active' : ''}>
      <Link to={to}>
        <span className="icon" aria-hidden>{emoji}</span>
        <span className="text">{label}</span>
      </Link>
    </li>
  );

  if (!user) return null;

  return (
    <footer className="footer">
      <nav className="nav">
        <ul>
          {navItem('/journal', 'Journaling', '‚úçÔ∏è')}
          {navItem('/entries', 'Entries', 'üìö')}
          {navItem('/insights', 'Insights', 'üìä')}
          {navItem('/goals', 'Goals', 'üéØ')}
          {navItem('/sage', 'MemoTalks', 'üí≠')}
          {navItem('/reminders', 'Reminders', '‚è∞')}
          {navItem('/settings', 'Settings', '‚öôÔ∏è')}
        </ul>
      </nav>
      <div className="footer-info">
        <p>&copy; 2026 MindSync. All rights reserved.</p>
      </div>
    </footer>
  );
}
