import React, { useState, useRef, useEffect } from 'react';
import './MemoTalks.css';

const MemoTalks = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      sender: 'memotalks',
      text: "Hello, I'm MemoTalks. I'm here to listen, support, and guide you through your thoughts and feelings. Share what's on your mind."
    }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [apiKey, setApiKey] = useState(localStorage.getItem('memotalks_api_key') || '');
  const [showApiModal, setShowApiModal] = useState(!apiKey);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const generateResponse = (userMessage) => {
    const lowerMessage = userMessage.toLowerCase();
    const words = userMessage.split(/\s+/);

    // Extract key information from user input
    const containsQuestion = userMessage.includes('?');
    
    // Emotion detection
    const sadKeywords = ['sad', 'depressed', 'down', 'lonely', 'miserable', 'terrible', 'awful', 'hurts', 'pain', 'cry', 'broken', 'lost', 'hopeless', 'devastated'];
    const happyKeywords = ['happy', 'great', 'amazing', 'wonderful', 'excited', 'proud', 'love', 'celebrate', 'success', 'win', 'achieved', 'awesome', 'fantastic', 'blessed'];
    const anxiousKeywords = ['doubt', 'unsure', 'uncertain', 'confused', 'unclear', 'worried', 'anxious', 'scared', 'afraid', 'nervous', 'stressed'];
    const motivationKeywords = ['motivate', 'inspire', 'encourage', 'help', 'stuck', 'stagnant', 'lazy', 'procrastinating'];
    const goalKeywords = ['goal', 'target', 'objective', 'dream', 'want', 'achieve', 'accomplish', 'improve', 'better'];
    const relationshipKeywords = ['friend', 'family', 'love', 'relationship', 'partner', 'parent', 'sibling', 'conflict', 'argument'];
    const workKeywords = ['work', 'job', 'career', 'boss', 'colleague', 'project', 'deadline', 'office'];
    const healthKeywords = ['health', 'fitness', 'exercise', 'sleep', 'anxiety', 'stress', 'mental', 'physical', 'wellness'];

    // Detect emotion category
    let emotionCategory = 'neutral';
    if (sadKeywords.some(keyword => lowerMessage.includes(keyword))) {
      emotionCategory = 'sad';
    } else if (happyKeywords.some(keyword => lowerMessage.includes(keyword))) {
      emotionCategory = 'happy';
    } else if (anxiousKeywords.some(keyword => lowerMessage.includes(keyword))) {
      emotionCategory = 'anxious';
    } else if (motivationKeywords.some(keyword => lowerMessage.includes(keyword))) {
      emotionCategory = 'motivation';
    }

    // Detect topic
    let topicCategory = 'general';
    if (goalKeywords.some(keyword => lowerMessage.includes(keyword))) {
      topicCategory = 'goals';
    } else if (relationshipKeywords.some(keyword => lowerMessage.includes(keyword))) {
      topicCategory = 'relationships';
    } else if (workKeywords.some(keyword => lowerMessage.includes(keyword))) {
      topicCategory = 'work';
    } else if (healthKeywords.some(keyword => lowerMessage.includes(keyword))) {
      topicCategory = 'health';
    }

    // Generate personalized response based on emotion and topic
    let response = generatePersonalizedResponse(emotionCategory, topicCategory, userMessage, containsQuestion);
    
    return response;
  };

  const generatePersonalizedResponse = (emotion, topic, message, isQuestion) => {
    const responses = {
      sad: {
        general: [
          "It's okay to feel down. üíô Difficult moments are chapters, not the whole story. You've overcome challenges before, and you have the strength to do it again.",
          "Your sadness shows you care deeply. üå± Take a breath. This feeling will pass, and you'll find clarity.",
          "Even on dark days, there's hope. You're stronger than you think. üí™ What's one small thing you could do to comfort yourself?",
          "Sadness is temporary, but your resilience is permanent. Let's find one thing to be grateful for today.",
          "It's brave to acknowledge your feelings. You're not alone. ü§ù What would help you feel better right now?"
