import React, { useState, useEffect } from 'react';
import { Bar, Doughnut, Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement, PointElement, LineElement } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement, PointElement, LineElement);

const Insights = () => {
  const [entries, setEntries] = useState([]);
  const [moodData, setMoodData] = useState({});
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEntries = async () => {
      try {
        const userId = localStorage.getItem('user');
        if (!userId) {
          setLoading(false);
          return;
        }

        const response = await fetch(`http://localhost:5000/api/entries/user/${userId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch entries');
        }
        const data = await response.json();
        setEntries(data);

        // Process mood data
        const moods = {};
        data.forEach(entry => {
          const date = new Date(entry.createdAt).toDateString();
          moods[date] = moods[date] || { positive: 0, negative: 0, neutral: 0, words: 0 };
          moods[date][entry.sentiment]++;
          moods[date].words += entry.wordCount || 0;
        });
        setMoodData(moods);

        // Generate summary with word count
        const totalEntries = data.length;
        const totalWords = data.reduce((sum, e) => sum + (e.wordCount || 0), 0);
        const avgWords = totalEntries > 0 ? Math.round(totalWords / totalEntries) : 0;
        const avgSentiment = data.reduce((acc, e) => {
          if (e.sentiment === 'positive') return acc + 1;
          if (e.sentiment === 'negative') return acc - 1;
          return acc;
        }, 0) / (totalEntries || 1);
        setSummary({
          totalEntries,
          totalWords,
          avgWords,
          avgSentiment: avgSentiment > 0 ? 'Positive' : avgSentiment < 0 ? 'Negative' : 'Neutral'
        });
        setLoading(false);
      } catch (err) {
        console.error('Error fetching entries:', err);
        setLoading(false);
      }
    };

    fetchEntries();
  }, []);

  const generateRecommendations = (slice, days) => {
    const recommendations = [];
    const moodCounts = { positive: 0, negative: 0, neutral: 0 };
    const tagCount = {};
    const daysActive = new Set();
    const recentEntries = slice.slice(-5);
    
    slice.forEach(e => {
      moodCounts[e.sentiment] = (moodCounts[e.sentiment] || 0) + 1;
      (e.tags || []).forEach(t => { tagCount[t] = (tagCount[t] || 0) + 1; });
      daysActive.add(new Date(e.date).toDateString());
    });

    // Check recent negative streak
    const recentMoods = recentEntries.map(e => e.sentiment);
    const negativeStreak = recentMoods.filter(m => m === 'negative').length;
    const positiveStreak = recentMoods.filter(m => m === 'positive').length;

    // Activity patterns
    const consistencyRate = (daysActive.size / days) * 100;
    
    // Generate personalized recommendations
    if (negativeStreak >= 2) {
      recommendations.push("ðŸ’™ You've been feeling down lately. Try going for a walk, listening to your favorite music, or calling a friend. Small steps can make a big difference!");
    }
    
    if (positiveStreak >= 3) {
      recommendations.push("ðŸŒŸ You're on a positive streak! Keep this momentum going. What made these days great? Do more of that!");
    }
    
    if (consistencyRate >= 80) {
      recommendations.push("ðŸ”¥ Amazing consistency! You've been journaling regularly - this habit is building self-awareness and emotional intelligence.");
    } else if (consistencyRate < 30 && days === 7) {
      recommendations.push("ðŸ“ Try journaling daily for just 2 minutes. Even brief reflections can boost mood and clarity!");
    }
    
    // Tag-based insights
    if (tagCount['exercise'] >= 3 || tagCount['gym'] >= 3 || tagCount['workout'] >= 3) {
      recommendations.push("ðŸ’ª You've been hitting the gym! Your consistency is impressive. Remember to fuel your body with protein-rich foods and stay hydrated!");
    }
    
    if (tagCount['work'] > tagCount['relaxation'] * 2) {
      recommendations.push("ðŸ§˜ You've been focused on work. Don't forget to schedule some 'me time' - your mental health matters too!");
    }
    
    if (moodCounts.negative > moodCounts.positive && slice.length > 3) {
      recommendations.push("ðŸŒˆ This period has been challenging. Consider: What's one small thing you can do today to care for yourself? You deserve kindness.");
    }
    
    if (moodCounts.positive > moodCounts.negative * 2) {
      recommendations.push("âœ¨ Your positive energy is shining through! Keep celebrating the wins, big and small.");
    }

    // Default motivation if no specific patterns
    if (recommendations.length === 0) {
      recommendations.push("ðŸŒ± Keep nurturing your mental wellness journey. Every entry is a step toward greater self-understanding!");
    }
    
    return recommendations;
  };

  const summarize = (days) => {
    const since = new Date();
    since.setDate(since.getDate() - days);
    const slice = entries.filter(e => new Date(e.createdAt) >= since);
    const total = slice.length;
    const totalWords = slice.reduce((sum, e) => sum + (e.wordCount || 0), 0);
    const avgWords = total > 0 ? Math.round(totalWords / total) : 0;
    const moodCounts = { positive:0, negative:0, neutral:0 };
    const tagCount = {};
    const daysActive = new Set();
    slice.forEach(e => {
      moodCounts[e.sentiment] = (moodCounts[e.sentiment]||0) + 1;
      (e.tags||[]).forEach(t => { tagCount[t] = (tagCount[t]||0)+1; });
      daysActive.add(new Date(e.createdAt).toDateString());
    });
