import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import CryptoJS from 'crypto-js';
import Sentiment from 'sentiment';
import './Journaling.css';

const Journaling = () => {
  const [entry, setEntry] = useState('');
  const [title, setTitle] = useState('');
  const [mood, setMood] = useState('calm');
  const [autoMood, setAutoMood] = useState('neutral');
  const [files, setFiles] = useState([]);
  const [images, setImages] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [transcription, setTranscription] = useState('');
  const [saving, setSaving] = useState(false);
  const [savedMsg, setSavedMsg] = useState('');
  const [wordCount, setWordCount] = useState(0);
  const recognitionRef = useRef(null);
  const navigate = useNavigate();
  const sentimentAnalyzer = useRef(new Sentiment());

  const moods = [
    { emoji: 'üòä', label: 'happy', color: '#fbbf24' },
    { emoji: 'üòî', label: 'sad', color: '#f87171' },
    { emoji: 'üòå', label: 'calm', color: '#60a5fa' },
    { emoji: 'üò†', label: 'angry', color: '#fb7185' },
    { emoji: 'üò¥', label: 'tired', color: '#a78bfa' },
    { emoji: 'ü§©', label: 'excited', color: '#ec4899' }
  ];

  const prompts = [
    "What made you smile today?",
    "Describe your biggest achievement this week",
    "What challenged you today?",
    "Who did you appreciate today and why?",
    "What are you grateful for?",
    "What's on your mind right now?",
    "Write about a moment that made you reflect",
    "What would you like to improve?"
  ];

  const handleEntryChange = (e) => {
    const text = e.target.value;
    setEntry(text);
    setWordCount(text.trim().split(/\s+/).filter(w => w.length > 0).length);
  };

  const handleFileUpload = (e) => {
    const uploadedFiles = Array.from(e.target.files || []);
    const images = uploadedFiles.filter(f => f.type.startsWith('image/'));
    const docs = uploadedFiles.filter(f => !f.type.startsWith('image/'));
    
    setImages([...images, ...images]);
    setFiles([...files, ...docs]);
  };

  const removeFile = (idx, type) => {
    if (type === 'image') {
      setImages(images.filter((_, i) => i !== idx));
    } else {
      setFiles(files.filter((_, i) => i !== idx));
    }
  };

  const startRecording = () => {
    if (!('webkitSpeechRecognition' in window)) {
      alert('Speech recognition not supported');
      return;
    }
    // eslint-disable-next-line no-undef
    const recognition = new webkitSpeechRecognition();
    recognition.continuous = false;
    recognition.interimResults = false;
    recognition.lang = 'en-US';

    recognition.onstart = () => setIsRecording(true);
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setTranscription(transcript);
      setEntry(prev => prev + ' ' + transcript);
    };
    recognition.onend = () => setIsRecording(false);

    recognitionRef.current = recognition;
    recognition.start();
  };

  const stopRecording = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
  };

  const insertPrompt = (prompt) => {
    setEntry(prev => prev + '\n\n' + prompt + '\n');
  };

  const saveEntry = async () => {
    const user = localStorage.getItem('user');
    if (!user || !entry.trim()) return;

    setSaving(true);

    // Convert files to data URLs for storage
    const fileObjs = await Promise.all(
      files.map(
        (f) =>
          new Promise((resolve) => {
            const reader = new FileReader();
            reader.onload = () =>
              resolve({
                name: f.name,
                type: f.type,
                size: f.size,
                dataUrl: reader.result,
              });
            reader.readAsDataURL(f);
          })
      )
    );

    const newEntry = {
      id: Date.now(),
      title: title || 'Untitled Entry',
      text: entry,
      files: fileObjs,
      mood: mood,
      date: new Date().toISOString(),
      tags: generateTags(entry),
      sentiment: analyzeSentiment(entry)
    };

    // Encrypt and store
    const encrypted = CryptoJS.AES.encrypt(JSON.stringify(newEntry), 'secret-key').toString();
    const entries = JSON.parse(localStorage.getItem(`${user}_entries`) || '[]');
    entries.push(encrypted);
    localStorage.setItem(`${user}_entries`, JSON.stringify(entries));

    // Reset form
    setEntry('');
    setTitle('');
    setMood('calm');
    setFiles([]);
    setImages([]);
    setTranscription('');
    setWordCount(0);
    setSaving(false);
    setSavedMsg('‚ú® Entry saved successfully!');
    setTimeout(() => setSavedMsg(''), 2000);
    // Navigate to entries page to view the saved item
    setTimeout(() => navigate('/entries'), 500);
  };

  const generateTags = (text) => {
    const words = text.toLowerCase().split(/\s+/);
    return words.filter(w => w.length > 3).slice(0, 5);
  };

  const analyzeSentiment = (text) => {
    const positiveWords = ['happy', 'good', 'great', 'awesome', 'love', 'amazing'];
    const negativeWords = ['sad', 'bad', 'terrible', 'awful', 'hate', 'horrible'];
    const words = text.toLowerCase().split(/\s+/);
    const positiveCount = words.filter(w => positiveWords.includes(w)).length;
    const negativeCount = words.filter(w => negativeWords.includes(w)).length;
    if (positiveCount > negativeCount) return 'positive';
    if (negativeCount > positiveCount) return 'negative';
    return 'neutral';
  };

  return (
    <div className="journaling-page">
      <div className="journaling-header">
        <h1>‚ú® Create Your Entry</h1>
        <p className="header-subtitle">Capture your thoughts, feelings, and moments that matter</p>
      </div>

      <div className="journaling-main">
        {/* Entry Form Section */}
        <div className="journaling-section">
          <h2>‚úçÔ∏è Write Your Story</h2>
          
          {/* Title Input */}
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Give your entry a title (optional)"
            className="entry-title-input"
            maxLength="100"
          />

          {/* Mood Selector */}
          <div className="mood-selector">
            <label>How are you feeling?</label>
            <div className="mood-options">
              {moods.map((m) => (
                <button
                  key={m.label}
                  className={`mood-btn ${mood === m.label ? 'active' : ''}`}
                  onClick={() => setMood(m.label)}
                  style={{
                    borderColor: mood === m.label ? m.color : 'rgba(255, 255, 255, 0.1)',
                    backgroundColor: mood === m.label ? `${m.color}20` : 'rgba(15, 15, 19, 0.6)'
                  }}
                >
                  <span className="mood-emoji">{m.emoji}</span>
                  <span className="mood-label">{m.label}</span>
                </button>
              ))}
            </div>
          </div>

          {/* Main Text Area */}
          <div className="textarea-container">
            <textarea
              value={entry}
              onChange={handleEntryChange}
              placeholder="Write your thoughts, feelings, and experiences here... Let your words flow freely"
              className="entry-textarea"
            />
            <div className="word-counter">
              <span>{wordCount} words</span>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="action-buttons">
            <button 
              className="btn-save" 
              onClick={saveEntry} 
              disabled={saving || !entry.trim()}
            >
              <span>üíæ</span>
              {saving ? 'Saving...' : 'Save Entry'}
            </button>
            <button 
              className="btn-clear"
              onClick={() => {
                setEntry('');
                setTitle('');
                setMood('calm');
                setFiles([]);
                setImages([]);
                setTranscription('');
              }}
            >
              <span>üóëÔ∏è</span>
              Clear All
            </button>
          </div>

          {savedMsg && (
            <div className="success-message">{savedMsg}</div>
          )}
        </div>

        {/* Writing Prompts Section */}
        <div className="journaling-section prompts-section">
          <h2>üí° Writing Prompts</h2>
          <p className="section-description">Get inspired with prompts. Click any to add it to your entry.</p>
          <div className="prompts-grid">
            {prompts.map((prompt, idx) => (
              <button
                key={idx}
                className="prompt-card"
                onClick={() => insertPrompt(prompt)}
                title="Click to add to your entry"
              >
                <span className="prompt-text">{prompt}</span>
                <span className="prompt-icon">‚Üí</span>
              </button>
            ))}
          </div>
        </div>

        {/* Media Upload Section */}
        <div className="journaling-section">
          <h2>üì∏ Add Media</h2>
          
          {/* Image Upload */}
          <div className="upload-section">
            <label className="upload-label">
              <span className="upload-icon">üñºÔ∏è</span>
              <span>Add Images</span>
              <input 
                type="file" 
                multiple 
                onChange={handleFileUpload} 
                accept="image/*"
                style={{ display: 'none' }}
              />
            </label>
            {images.length > 0 && (
              <div className="media-preview-grid">
                {images.map((img, i) => (
                  <div key={i} className="media-item">
                    <img 
                      src={URL.createObjectURL(img)} 
                      alt={`Preview ${i}`}
                      className="media-thumb"
                    />
                    <button
                      className="remove-media-btn"
                      onClick={() => removeFile(i, 'image')}
                    >
                      ‚úï
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* File Upload */}
          <div className="upload-section">
            <label className="upload-label">
              <span className="upload-icon">üìé</span>
              <span>Attach Files</span>
              <input 
                type="file" 
                multiple 
                onChange={handleFileUpload} 
                accept=".pdf,.doc,.docx,.txt,video/*"
                style={{ display: 'none' }}
              />
            </label>
            {files.length > 0 && (
              <div className="file-list">
                {files.map((f, i) => (
                  <div key={i} className="file-item">
                    <span className="file-icon">üìÑ</span>
                    <span className="file-name">{f.name}</span>
                    <button
                      className="remove-file-btn"
                      onClick={() => removeFile(i, 'file')}
                    >
                      ‚úï
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Voice Recording Section */}
        <div className="journaling-section">
          <h2>üé§ Voice Recording</h2>
          <p className="section-description">Record your thoughts and they'll be transcribed automatically</p>
          <button 
            className={`btn-voice ${isRecording ? 'recording' : ''}`}
            onClick={isRecording ? stopRecording : startRecording}
          >
            <span className="voice-icon">{isRecording ? '‚èπÔ∏è' : 'üé§'}</span>
            {isRecording ? 'Stop Recording' : 'Start Voice Recording'}
          </button>
          {transcription && (
            <div className="transcription-box">
              <label>Transcribed Text:</label>
              <p>{transcription}</p>
            </div>
          )}
        </div>

        {/* Tips Section */}
        <div className="journaling-section tips-section">
          <h2>‚ú® Journaling Tips</h2>
          <div className="tips-grid">
            <div className="tip-card">
              <span className="tip-icon">üéØ</span>
              <h3>Be Honest</h3>
              <p>Write freely without judgment. Your journal is a safe space for your true thoughts.</p>
            </div>
            <div className="tip-card">
              <span className="tip-icon">‚è∞</span>
              <h3>Make it Regular</h3>
              <p>Build a habit by journaling at the same time each day. Even 5 minutes counts!</p>
            </div>
            <div className="tip-card">
              <span className="tip-icon">üåà</span>
              <h3>Express Everything</h3>
              <p>Don't limit yourself. Write about emotions, dreams, goals, and everyday moments.</p>
            </div>
            <div className="tip-card">
              <span className="tip-icon">üîÑ</span>
              <h3>Review & Reflect</h3>
              <p>Go back and read your entries. You'll notice patterns and personal growth.</p>
            </div>
            <div className="tip-card">
              <span className="tip-icon">üìö</span>
              <h3>No Rules</h3>
              <p>There's no "right" way to journal. Bullet points, paragraphs, drawings - all work!</p>
            </div>
            <div className="tip-card">
              <span className="tip-icon">üé®</span>
              <h3>Get Creative</h3>
              <p>Add images, voice notes, or files to enrich your entries and memories.</p>
            </div>
          </div>
        </div>
      </div>

      <style>{`
        @keyframes slideIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .journaling-page { animation: slideIn 0.5s ease; }
      `}</style>
    </div>
  );
};

export default Journaling;
